import React, { useState, useEffect, useRef } from "react";
import logo from "../images/vertu-motors-logo.png";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faChevronDown } from "@fortawesome/free-solid-svg-icons";
const Navbar = () => {
    const [activeMenu, setActiveMenu] = useState("");
    const dropdownRef = useRef(null);

    useEffect(() => {
        const handleOutsideClick = (event) => {
            if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
                setActiveMenu("");
            }
        };

        document.addEventListener("mousedown", handleOutsideClick);

        return () => {
            document.removeEventListener("mousedown", handleOutsideClick);
        };
    }, []);

    const toggleMenu = (menuName) => {
        setActiveMenu(menuName === activeMenu ? "" : menuName);
    };

    const isMenuActive = (menuName) => {
        return menuName === activeMenu;
    };

    return (
        <header className="header">
            <div className="logo-container">
                <img className="logo-image" src={logo} alt="Logo" />
            </div>
            <nav>
                
                    <p className={`menu-item ${isMenuActive("newCars") ? "active" : ""}`}>
                        <a href="#" onClick={() => toggleMenu("newCars")}>
                            New cars
                            <FontAwesomeIcon icon={faChevronDown} />
                        </a>
                        {isMenuActive("newCars") && (
                            <div className="dropdown-content" ref={dropdownRef}>
                                <a href="#">Option 1</a>
                                <a href="#">Option 2</a>
                                <a href="#">Option 3</a>
                            </div>
                        )}
                    </p>
                    <p className={`menu-item ${isMenuActive("usedCars") ? "active" : ""}`}>
                        <a href="#" onClick={() => toggleMenu("usedCars")}>
                            Used cars
                            <FontAwesomeIcon icon={faChevronDown} />
                        </a>
                        {isMenuActive("usedCars") && (
                            <div className="dropdown-content" ref={dropdownRef}>
                                <a href="#">Option 1</a>
                                <a href="#">Option 2</a>
                                <a href="#">Option 3</a>
                            </div>
                        )}
                    </p>
                    <p className={`menu-item ${isMenuActive("servicing") ? "active" : ""}`}>
                        <a href="#" onClick={() => toggleMenu("servicing")}>
                            Servicing
                            <FontAwesomeIcon icon={faChevronDown} />
                        </a>
                        {isMenuActive("servicing") && (
                            <div className="dropdown-content" ref={dropdownRef}>
                                <a href="#">Option 1</a>
                                <a href="#">Option 2</a>
                                <a href="#">Option 3</a>
                            </div>
                        )}
                    </p>
                    <p className={`menu-item ${isMenuActive("commercials") ? "active" : ""}`}>
                        <a href="#" onClick={() => toggleMenu("commercials")}>
                            Commercials
                            <FontAwesomeIcon icon={faChevronDown} />
                        </a>
                        {isMenuActive("commercials") && (
                            <div className="dropdown-content" ref={dropdownRef}>
                                <a href="#">Option 1</a>
                                <a href="#">Option 2</a>
                                <a href="#">Option 3</a>
                            </div>
                        )}
                    </p>
                    <p className="search-box">
                        <input type="text" placeholder='Search: "Make, model or location"' />
                        <i className="fas fa-microphone"></i>
                    </p>
                
                        <i className="fas fa-map-marker-alt"></i>
                    
                    
                        <i className="far fa-heart"></i>
                    
                   
                        <i className="fas fa-sign-in-alt"></i>
                    
                
            </nav>
        </header>
    )
}

export default Navbar
